  nomscategorie: {
    type: String,
    required: true
  },
  imagescat: {
    type: String,
    required: false
  },
  categorieID: {
    type: mongoose.Schema.Types.ObjectId,
    ref: Categorie
  }
});
module.exports = mongoose.model('scategorie', scategorieSchema);
