"use strict";

var mongoose = require("mongoose");
var Scategorie = require("./scategorie.js");
var articleSchema = mongoose.Schema({
  reference: {
    type: String,
    required: true,
    unique: true
  },
  designation: {
    type: String,
    required: true,
    unique: true
  },
  prix: {
    type: Number,
    required: false
  },
  marque: {
    type: String,
    required: true
  },
  qtestock: {
    type: Number,
    required: false
  },
  imageart: {
    type: String,
    required: false
  },
  scategorieID: {
    type: mongoose.Schema.Types.ObjectId,
    ref: Scategorie
  }
});
module.exports = mongoose.model('article', articleSchema);
